import { createApp } from 'vue'
import App from './App.vue'
import { FontAwesomeIcon } from './plugins/fontawesome'
import { VersionPlugin } from './mixins/versionMixin'  // <-- Agregar esta línea
import router from './router'

const app = createApp(App)

// Usar router primero
app.use(router)

// Instalar plugin de versionado CONFIGURACIÓN
app.use(VersionPlugin, {
  autoSave: true,              // Activar autoguardado
  autoSaveInterval: 30000,     // 30 segundos
  maxVersions: 10,            // Máximo 10 versiones por documento
  enableNotifications: true,   // Mostrar notificaciones
  backupToStorage: true,      // Hacer backups automáticos
  language: 'es'              // Español por defecto
})

// Registrar componente FontAwesomeIcon globalmente
app.component('font-awesome-icon', FontAwesomeIcon)

// Montar la aplicación
app.mount('#app')

// Opcional: Configurar comportamiento de desarrollo
if (process.env.NODE_ENV === 'development') {
  console.log('Aplicación Vue iniciada en modo desarrollo')
  console.log('Sistema de versiones instalado y configurado')
}
