        const insertionStart = config.type.isInline ? start : start - 1

        tr.insert(insertionStart, config.type.create(attributes)).delete(tr.mapping.map(start), tr.mapping.map(end))
      }

      tr.scrollIntoView()
    },
    undoable: config.undoable,
  })
}
